import React, { useState, useEffect } from "react";
import "./Popular.css";

const Popular = () => {
  const [popularProducts, setPopularProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch("http://localhost:4000/popularinwomen")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch popular products");
        }
        return response.json();
      })
      .then((data) => {
        setPopularProducts(data);
        setIsLoading(false);
      })
      .catch((err) => {
        setError(err.message);
        setIsLoading(false);
      });
  }, []);

  return (
    <div className="popular-section">
      <div className="popular-header">
        <h1>Popular in Women</h1>
        <p>Discover our top picks for women's fashion this season.</p>
      </div>
      <div className="popular-container">
        {isLoading && <div className="loading">Loading products...</div>}
        {error && <div className="error-message">{error}</div>}
        {!isLoading &&
          !error &&
          popularProducts.map((item, index) => (
            <div className="popular-card" key={index}>
              <div className="card-image">
                <img src={item.image} alt={item.name} />
                <div className="overlay">
                  <button className="action-btn">Add to Cart</button>
                  <button className="action-btn">View Details</button>
                </div>
              </div>
              <div className="card-details">
                <h2>{item.name}</h2>
                <div className="price">
                  <span className="new-price">${item.new_price}</span>
                  <span className="old-price">${item.old_price}</span>
                </div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Popular;
