import React, { useContext } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';
import { useNavigate } from 'react-router-dom';

const CartItems = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart, addToCart } = useContext(ShopContext);
  const navigate = useNavigate();

  const isLoggedIn = !!localStorage.getItem('auth-token'); // Check if the user is logged in
  const isCartEmpty = all_product.every(e => cartItems[e.id] <= 0); // Determine if cart is empty

  const handleProceedToCheckout = () => {
    if (!isLoggedIn) {
      alert('Please log in to proceed to checkout.');
      return;
    }
    navigate('/confirmorder');
  };

  return (
    <div className="cartitems-container">
      {/* Page Title */}
      <header className="cart-header">
        <h1 className="cart-title">Your Shopping Cart</h1>
        <p className="cart-description">
          Review the items in your cart before proceeding to checkout. Adjust quantities or remove items as needed.
        </p>
      </header>

      {/* Cart Table Header */}
      <div className="cartitems-header">
        <p className="cart-column">Products</p>
        <p className="cart-column">Title</p>
        <p className="cart-column">Price</p>
        <p className="cart-column">Quantity</p>
        <p className="cart-column">Total</p>
        <p className="cart-column">Remove</p>
      </div>
      <hr className="divider" />

      {/* Cart Items */}
      {all_product.map((e) => {
        if (cartItems[e.id] > 0) {
          return (
            <div key={e.id} className="cart-item-row">
              <div className="cart-item">
                <img src={e.image} alt={e.name} className="cart-item-image" />
                <p className="cart-item-title">{e.name}</p>
                <p className="cart-item-price">TK {e.new_price}</p>

                <div className="cart-quantity">
                  <button className="cart-quantity-btn" onClick={() => removeFromCart(e.id)}>-</button>
                  <span className="cart-quantity-count">{cartItems[e.id]}</span>
                  <button className="cart-quantity-btn" onClick={() => addToCart(e.id)}>+</button>
                </div>

                <p className="cart-item-total">TK {e.new_price * cartItems[e.id]}</p>
                <img
                  className="cart-remove-icon"
                  src={remove_icon}
                  onClick={() => removeFromCart(e.id)}
                  alt="Remove"
                />
              </div>
              <hr className="divider" />
            </div>
          );
        }
        return null;
      })}

      {/* Footer Section */}
      <div className="cart-footer">
        {isCartEmpty ? (
          <div className="cart-empty">
            <h2 className="cart-empty-title">Your cart is empty!</h2>
            <p className="cart-empty-message">
              Looks like you haven't added anything to your cart yet. Explore our wide range of products and start shopping!
            </p>
          </div>
        ) : (
          <div className="cart-totals">
            <h1 className="cart-totals-title">Cart Totals</h1>
            <div className="cart-totals-details">
              <div className="cart-totals-row">
                <p>Subtotal</p>
                <p>TK {getTotalCartAmount()}</p>
              </div>
              <hr className="divider" />
              <div className="cart-totals-row">
                <p>Shipping Fee</p>
                <p>Free</p>
              </div>
              <hr className="divider" />
              <div className="cart-totals-row">
                <h3>Total</h3>
                <h3>TK {getTotalCartAmount()}</h3>
              </div>
            </div>
            <button
              className="checkout-btn"
              onClick={handleProceedToCheckout}
              disabled={isCartEmpty}
            >
              PROCEED TO CHECKOUT
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartItems;
