import React, { useContext, useState } from 'react';
import './ProductDisplay.css';
import star_icon from "../Assets/star_icon.png";
import star_dull_icon from "../Assets/star_dull_icon.png";
import { ShopContext } from '../../Context/ShopContext';
import { useNavigate } from 'react-router-dom';

const ProductDisplay = ({ product }) => {
  const { addToCart } = useContext(ShopContext);
  const [showPopup, setShowPopup] = useState(false); 
  const navigate = useNavigate();

  const isLoggedIn = !!localStorage.getItem('auth-token'); // Check if the user is logged in

  const handleAddToCart = (id) => {
    addToCart(id);
    setShowPopup(true); 

    setTimeout(() => {
      setShowPopup(false);
    }, 3000);
  };

  const handleTryVirtually = (id) => {
    if (isLoggedIn) {
      navigate(`/virtualtry/${id}`);
    } else {
      alert('Please log in to access this feature.');
    }
  };

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img">
          <img 
            className='productdisplay-main-img' 
            src={product.image} 
            alt={product.name} 
          />
        </div>
      </div>

      <div className="productdisplay-right">
        <h1>{product.name}</h1>

        <div className="productdisplay-right-stars">
          {[...Array(4)].map((_, index) => (
            <img key={index} src={star_icon} alt="Star" />
          ))}
          <img src={star_dull_icon} alt="Dull Star" />
          <p>(122)</p>
        </div>

        <div className="productdisplay-right-prices">
          <span className="productdisplay-right-price-old">TK {product.old_price}</span>
          <span className="productdisplay-right-price-new">TK {product.new_price}</span>
        </div>

        <p className="productdisplay-right-description">
          For casual wear.
        </p>

        <button onClick={() => handleTryVirtually(product.id)}>Try Virtually</button>
        <button onClick={() => handleAddToCart(product.id)}>Add to Cart</button>
      </div>

      {showPopup && (
        <div className="popup">
          <p>Product added to cart!</p>
        </div>
      )}
    </div>
  );
};

export default ProductDisplay;
