import React, { useState } from "react";
import axios from "axios";

const TryVirtually = () => {
  const [personImage, setPersonImage] = useState(null);
  const [clothImage, setClothImage] = useState(null);
  const [resultImage, setResultImage] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const handlePersonImageChange = (e) => {
    setPersonImage(e.target.files[0]);
  };

  const handleClothImageChange = (e) => {
    setClothImage(e.target.files[0]);
  };

  const handleTryOn = async () => {
    if (!personImage || !clothImage) {
      setErrorMessage("Please upload both person and cloth images.");
      return;
    }

    const formData = new FormData();
    formData.append("personImage", personImage);
    formData.append("clothImage", clothImage);

    try {
      const response = await axios.post(
        "http://localhost:4000/process-vitons/1", // Replace '1' with the actual productId if dynamic
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data && response.data.personImage && response.data.clothImage) {
        const { personImage, clothImage } = response.data;
        setResultImage(`http://localhost:4000${personImage}`);
        setErrorMessage(""); // Clear any previous error
      } else {
        setErrorMessage("Unexpected response from the server.");
      }
    } catch (error) {
      console.error("Error during try-on:", error);
      setErrorMessage(
        error.response?.data?.error || "Failed to process the try-on request."
      );
    }
  };

  return (
    <div>
      <h1>Virtual Try-On</h1>
      <div>
        <label>Upload Person Image:</label>
        <input type="file" accept="image/*" onChange={handlePersonImageChange} />
      </div>
      <div>
        <label>Upload Cloth Image:</label>
        <input type="file" accept="image/*" onChange={handleClothImageChange} />
      </div>
      <button onClick={handleTryOn}>Try On</button>

      {resultImage && (
        <div>
          <h2>Result:</h2>
          <img src={resultImage} alt="Try-On Result" style={{ width: "300px" }} />
        </div>
      )}

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
    </div>
  );
};

export default TryVirtually;
