import React, { useContext, useState } from 'react';
import './ProductDisplay.css';
import star_icon from "../Assets/star_icon.png";
import star_dull_icon from "../Assets/star_dull_icon.png";
import { ShopContext } from '../../Context/ShopContext';
import { useNavigate } from 'react-router-dom';

const ProductDisplay = (props) => {
  const { product } = props;
  const { addToCart } = useContext(ShopContext);
  const [showPopup, setShowPopup] = useState(false); 
  const navigate = useNavigate();

  const isLoggedIn = !!localStorage.getItem('auth-token'); // Check if the user is logged in

  const handleAddToCart = (id) => {
    addToCart(id);
    setShowPopup(true); 

    setTimeout(() => {
      setShowPopup(false);
    }, 3000);
  };

  const handleTryVirtually = (id) => {
    if (isLoggedIn) {
      navigate(`/virtualtry/${id}`);
    } else {
      alert('Please log in to access this feature.');
    }
  };

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          <img src={product.image} alt="" />
          <img src={product.image} alt="" />
          <img src={product.image} alt="" />
          <img src={product.image} alt="" />
        </div>
        <div className="productdisplay-img">
          <img className='productdisplay-main-img' src={product.image} alt="" />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        <div className="productdisplay-right-stars">
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_icon} alt="" />
          <img src={star_dull_icon} alt="" />
          <p>(122)</p>
        </div>
        <div className="productdisplay-right-prices">
          <div className="productdisplay-right-price-old">TK {product.old_price}</div>
          <div className="productdisplay-right-price-new">TK {product.new_price}</div>
        </div>
        <div className="productdisplay-right-description">
          For casual wear.
        </div>

        <div className="productdisplay-details">
          <h3>Product Details:</h3>
          <ul>
            <li>Fabric: 100% Cotton</li>
            <li>Color: {product.color}</li>
            <li>Sizes Available: {product.sizes.join(", ")}</li>
            <li>Care Instructions: Machine wash cold, tumble dry low</li>
          </ul>
        </div>

        <div className="productdisplay-specifications">
          <h3>Specifications:</h3>
          <ul>
            <li>Length: 25 inches</li>
            <li>Width: 18 inches</li>
            <li>Weight: 200g</li>
            <li>Shipping: Free on orders above 1000 TK</li>
          </ul>
        </div>

        <button onClick={() => handleTryVirtually(product.id)}>Try-Virtually</button>
        <button onClick={() => handleAddToCart(product.id)}>ADD TO CART</button>

        <div className="productdisplay-reviews">
          <h3>Customer Reviews</h3>
          <div className="reviews-list">
            <div className="review-item">
              <p><strong>John Doe:</strong> Great product! Really comfy and stylish.</p>
            </div>
            <div className="review-item">
              <p><strong>Jane Smith:</strong> Excellent quality, I love it!</p>
            </div>
            <button>View more reviews</button>
          </div>
        </div>

        <div className="related-products">
          <h3>Related Products</h3>
          <div className="related-product-item">
            <img src={product.image} alt="Related Product" />
            <p>{product.name}</p>
            <button onClick={() => handleAddToCart(product.id)}>Add to Cart</button>
          </div>
          <div className="related-product-item">
            <img src={product.image} alt="Related Product" />
            <p>{product.name}</p>
            <button onClick={() => handleAddToCart(product.id)}>Add to Cart</button>
          </div>
        </div>
      </div>

      {showPopup && (
        <div className="popup">
          <p>Product added to cart!</p>
        </div>
      )}
    </div>
  );
};

export default ProductDisplay;
