import React, { useState, useCallback, useRef, useContext } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './VirtualTry.css';
import { ShopContext } from '../../Context/ShopContext';
import { useParams } from 'react-router-dom';

const VirtualTry = () => {
  const { productId } = useParams();
  const [image, setImage] = useState(null); // User-provided person image
  const [isCameraOpen, setIsCameraOpen] = useState(false); // Camera modal state
  const [resultImage, setResultImage] = useState(null); // Result from VITON
  const [errorMessage, setErrorMessage] = useState(''); // Error messages
  const [isLoading, setIsLoading] = useState(false); // Loading state
  const webcamRef = useRef(null);
  const { addToCart, all_product } = useContext(ShopContext);

  // Find the selected product
  const product = all_product.find((p) => p.id === Number(productId));

  // Capture image from webcam
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
    setIsCameraOpen(false);
  }, [webcamRef]);

  // Handle image upload
  const handleImageUpload = (event) => {
    setImage(URL.createObjectURL(event.target.files[0]));
  };

  // Open webcam modal
  const handleOpenCamera = () => {
    setIsCameraOpen(true);
  };

  // Close webcam modal
  const closeModal = () => {
    setIsCameraOpen(false);
  };

  // Add product to cart
  const handleAddToCart = () => {
    if (product) {
      addToCart(product.id);
      alert('Product added to cart!');
    }
  };

  // Handle the virtual try-on process
const handleTryOn = async () => {
  if (!image) {
    setErrorMessage('Please upload or capture an image of yourself.');
    return;
  }

  if (!product?.image) {
    setErrorMessage('Clothing image is unavailable. Please try again later.');
    return;
  }

  const formData = new FormData();

  // Convert base64 image to a Blob if captured via webcam
  if (image.startsWith('data:image/')) {
    const base64Response = await fetch(image);
    const blob = await base64Response.blob();
    formData.append('userImage', blob, 'user.jpg');
  } else {
    // Use a File object for uploaded images
    const response = await fetch(image);
    const blob = await response.blob();
    formData.append('userImage', blob, 'user.jpg');
  }

  // Fetch product image and convert to Blob
  const productResponse = await fetch(product.image);
  const productBlob = await productResponse.blob();
  formData.append('clothingImage', productBlob, 'clothing.jpg');

  try {
    setIsLoading(true);
    const response = await axios.post(
      'http://localhost:4000/upload-images',
      formData,
      {
        headers: { 'Content-Type': 'multipart/form-data' },
      }
    );

    if (response.data && response.data.userImagePath) {
      setResultImage(`http://localhost:4000/${response.data.userImagePath}`); // Adjust based on backend response
      setErrorMessage('');
    } else {
      setErrorMessage('Something went wrong! Please try again.');
    }
  } catch (error) {
    console.error(error);
    setErrorMessage('Failed to process the try-on request.');
  } finally {
    setIsLoading(false);
  }
};


  return (
    <div className="virtual-try-page">
      <div className="virtual-try-container">
        <h1 className="virtual-try-title">Virtual Try-On Experience</h1>
        <p className="virtual-try-description">
          Upload an image or use your camera to try on products virtually!
        </p>

        {product && (
          <div>
            <h2>{product.name}</h2>
            <img src={product.image} alt={product.name} className="preview-image" />
          </div>
        )}

        <div className="image-comparison-container">
          {image && (
            <div className="image-preview">
              <h3 className="preview-title">Your Uploaded Image</h3>
              <img src={image} alt="Preview" className="preview-image" />
            </div>
          )}
          {resultImage && (
            <div className="image-rendered">
              <h3 className="rendered-title">Virtual Try-On Result</h3>
              <img src={resultImage} alt="Rendered" className="rendered-image" />
            </div>
          )}
        </div>

        <div className="button-section">
          <button onClick={handleOpenCamera} className="button open-camera-button">
            Open Camera
          </button>
          <label htmlFor="file-upload" className="button upload-image-button">
            Upload Image
          </label>
          <input
            id="file-upload"
            type="file"
            accept="image/*"
            onChange={handleImageUpload}
            className="file-input"
            style={{ display: 'none' }}
          />
        </div>

        {isCameraOpen && (
          <div className="camera-modal">
            <div className="camera-overlay" onClick={closeModal}></div>
            <div className="camera-content">
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
                className="webcam"
              />
              <div className="camera-actions">
                <button onClick={captureImage} className="btn capture-button">
                  Capture
                </button>
                <button onClick={closeModal} className="btn close-button">
                  Close
                </button>
              </div>
            </div>
          </div>
        )}

        <div className="button-section">
          <button onClick={handleTryOn} className="button try-on-button">
            {isLoading ? 'Processing...' : 'Try On'}
          </button>
          <button onClick={handleAddToCart} className="button add-to-cart-button">
            Add to Cart
          </button>
        </div>

        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </div>
    </div>
  );
};

export default VirtualTry;
