import React, { useState, useCallback, useRef, useContext } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './VirtualTry.css';
import { ShopContext } from '../../Context/ShopContext';
import { useParams } from 'react-router-dom';

const VirtualTry = () => {
  const { productId } = useParams();
  const [image, setImage] = useState(null); // User-provided person image
  const [isCameraOpen, setIsCameraOpen] = useState(false); // Camera modal state
  const [resultImage, setResultImage] = useState(null); // Result from VITON
  const [errorMessage, setErrorMessage] = useState(''); // Error messages
  const webcamRef = useRef(null);
  const { all_product } = useContext(ShopContext);

  // Find the selected product
  const product = all_product.find((p) => p.id === Number(productId));

  // Capture image from webcam
  const captureImage = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);
    setIsCameraOpen(false);
  }, [webcamRef]);

  // Handle image upload
  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setImage(reader.result);
      reader.readAsDataURL(file);
    }
  };

  // Handle virtual try-on request
  const handleVirtualTryOn = async () => {
    try {
      const formData = new FormData();
      formData.append('personImage', image);
      formData.append('clothImage', product.image); // Assuming the product has an image URL

      const response = await axios.post(`http://localhost:4000/process-vitons/${productId}`, formData);
      setResultImage(response.data.result); // Set the result image URL from the response
    } catch (error) {
      setErrorMessage("Error processing virtual try-on.");
      console.error("Error:", error);
    }
  };

  return (
    <div className="virtual-try-on-container">
      <h1>Virtual Try-On</h1>
      <div className="webcam-container">
        {!isCameraOpen && (
          <button onClick={() => setIsCameraOpen(true)}>Open Camera</button>
        )}

        {isCameraOpen && (
          <>
            <Webcam
              audio={false}
              ref={webcamRef}
              screenshotFormat="image/jpeg"
              width="100%"
              videoConstraints={{
                facingMode: "user",
              }}
            />
            <button onClick={captureImage}>Capture</button>
          </>
        )}

        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
        />
      </div>

      {image && (
        <div className="image-preview">
          <h2>Preview of Captured Image:</h2>
          <img src={image} alt="Captured" />
        </div>
      )}

      {resultImage && (
        <div className="result-image">
          <h2>Try-On Result:</h2>
          <img src={resultImage} alt="Try-On Result" />
        </div>
      )}

      {errorMessage && <p>{errorMessage}</p>}

      <button onClick={handleVirtualTryOn}>Try On Virtual Clothing</button>
    </div>
  );
};

export default VirtualTry;
