import React, { useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { ShopContext } from '../../';
import axios from 'axios';
import './VirtualTry.css';

const VirtualTry = () => {
  const { productId } = useParams();
  const { getProductDetails, addToCart } = useContext(ShopContext);
  const product = getProductDetails(productId);

  const [userImage, setUserImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [resultImage, setResultImage] = useState(null);

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUserImage(file);
      setPreviewImage(URL.createObjectURL(file));
    }
  };

  const handleCapture = async () => {
    const video = document.querySelector('#videoElement');
    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    context.drawImage(video, 0, 0, canvas.width, canvas.height);
    const imageBlob = await new Promise((resolve) => canvas.toBlob(resolve, 'image/jpeg'));
    const file = new File([imageBlob], 'captured.jpg', { type: 'image/jpeg' });

    setUserImage(file);
    setPreviewImage(URL.createObjectURL(file));
  };

  const handleTryOn = async () => {
    if (!userImage) {
      alert('Please upload or capture an image first!');
      return;
    }

    const formData = new FormData();
    formData.append('personImage', userImage);

    try {
      const response = await axios.post(`/process-vitons/${productId}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (response.data && response.data.result) {
        setResultImage(response.data.result); // The URL of the processed image
      } else {
        alert('Something went wrong! Please try again.');
      }
    } catch (error) {
      console.error(error);
      alert('Error processing the try-on request.');
    }
  };

  return (
    <div className="virtual-try-container">
      <h1>Virtual Try-On</h1>

      <div className="product-section">
        <h2>{product.name}</h2>
        <img src={product.image} alt={product.name} className="product-image" />
        <p>{product.description}</p>
        <button onClick={() => addToCart(productId)}>Add to Cart</button>
      </div>

      <div className="user-image-section">
        <h2>Upload Your Image</h2>
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        <video id="videoElement" autoPlay hidden></video>
        <button onClick={handleCapture}>Capture from Camera</button>
        {previewImage && <img src={previewImage} alt="Preview" className="preview-image" />}
      </div>

      <div className="try-on-section">
        <button onClick={handleTryOn}>Try On</button>
        {resultImage && <img src={resultImage} alt="Try-On Result" className="result-image" />}
      </div>
    </div>
  );
};

export default VirtualTry;
